mapper.update:
UPDATE FAQ
FAQ_USE=#{FAQ_USE}, FAQ_ANSWER=#{FAQ_ANSWER},
WHERE FAQ_NO=#{FAQ_NO}

update.foreach:
			UPDATE FAQ
			SET FAQ_USE=#{item.FAQ_USE}
 			WHERE FAQ_NO=#{item.FAQ_NO}

mapper.select:
SELECT FAQ_NO, FAQ_ASK, FAQ_ANSWER, FAQ_USE, FAQ_CATEGORYNUM, CATEGORY_NAME, CATEGORY_USE FROM FAQ ,CATEGORY WHERE FAQ.FAQ_CATEGORYNUM = CATEGORY.CATEGORY_NO AND CATEGORY_USE = 1
AND FAQ_CATEGORYNUM = #{FAQ_CATEGORYNUM}
ORDER BY FAQ_USE DESC , FAQ_CATEGORYNUM ASC, FAQ_ASK ASC
